<script>
$(document).ready(function () {
$('a.pull-right').text('Next');
$('a.pull-left').text('Previous');
var r = $('<div style="float: right;" class="col-md-6"><button  class="btn btn-register btn-block submit" onclick="reg()" style="display: none;">Start Trading</button></div>');
$(".actionBar").append(r);
$('.submit').click(function () {
$(".stepContainer").removeAttr("style");
})
});

$.validator.addMethod("password", function (value) {
var pattern = /^(?=.*\d)(?=.*[a-z])(?=.*[A-Z]).{6,15}$/;
return pattern.test(value);
}, 'Password rules: Minimum length has to be 6, Maximum length has to be 15, Has to contain at least 1 Capital letter, Has to contain at least 1 Small letter, Has to contain at least 1 Number');

var uiSmartWizard = function () {

if ($(".wizard").length > 0) {

//Check count of steps in each wizard
$(".wizard > ul").each(function () {
$(this).addClass("steps_" + $(this).children("li").length);
});

if ($(".register").length > 0) {

var validator = $(".register").validate({
rules: {
firstname: {required: true, minlength: 2, maxlength: 80},
middlename: {minlength: 2, maxlength: 80},
lastname: {required: true, minlength: 2, maxlength: 80},
password: {required: true, password: true},
password_confirmation: {required: true, equalTo: "[name='password']"},
recoveryquestions: {required: true},
answer: {required: true, minlength: 2, maxlength: 80},
tradingplatform: {required: true},
currency: {required: true},
promocode: {minlength: 2, maxlength: 80},
address: {required: true, minlength: 2, maxlength: 80},
city: {required: true, minlength: 2, maxlength: 80},
country: {required: true},
postcode: {minlength: 2, maxlength: 80},
phonecountrycode: {number: true, minlength: 1, maxlength: 4},
phonenumber: {number: true, minlength: 5, maxlength: 12},
email: {required: true, email: true, minlength: 2, maxlength: 80},
email_confirmation: {required: true, equalTo: "[name='email']", email: true, minlength: 2, maxlength: 80},
dateofbirthday: {required: true},
dateofbirthmonth: {required: true},
dateofbirthyear: {required: true},
identificationnumber: {required: true, minlength: 2, maxlength: 80},
knowcfd: {required: true},
tradedcfd: {required: true},
solebenefical: {required: true},
hearaboutus: {required: true},
terms: {required: true},
privacy: {required: true},
riskdisclosure: {required: true}
}
});

}

$(".wizard").smartWizard({
onLeaveStep: function (obj) {
var wizard = obj.parents(".wizard");

if (wizard.hasClass("wizard-validation")) {

var valid = true;

$('input,textarea,select', $(obj.attr("href"))).each(function (i, v) {
valid = validator.element(v) && valid;
});

if (!valid) {
wizard.find(".stepContainer").removeAttr("style");
validator.focusInvalid();
return false;
}

}

return true;
},

onShowStep: function (obj) {
var wizard = obj.parents(".wizard");

if (wizard.hasClass("show-submit")) {

var step_num = obj.attr('rel');
var step_max = obj.parents(".anchor").find("li").length;

if (step_num == step_max) {
wizard.find(".actionBar .pull-right").css("display", "none");
wizard.find(".actionBar .btn-primary").css("display", "none");
wizard.find(".actionBar .btn-register").css("display", "block");
} else {
wizard.find(".actionBar .pull-right").css("display", "block");
wizard.find(".actionBar .btn-primary").css("display", "none");
wizard.find(".actionBar .btn-register").css("display", "none");
}

if (step_num == 1) {
wizard.find(".actionBar .pull-left").css("display", "none");
} else {
wizard.find(".actionBar .pull-left").css("display", "block");
}
}
return true;
}
});
}

}

uiSmartWizard();
</script>
